const express=require("express"),router=express(),userModel=require("../models/users"),productModel=require("../models/products");router.get("/",(e,r)=>{r.render("index.ejs",{message:""})}),router.post("/",(e,r)=>{userModel.find({email:e.body.email}).exec().then(e=>{e.length>=1?r.render("store.ejs",{userName:e[0].name}):r.render("index.ejs",{message:"We did not find an account associated with this email"})})}),router.get("/getUser",(e,r)=>{userModel.find({name:e.query.name}).exec().then(e=>{console.log(e),r.json(e)})}),module.exports=router;